function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(j){var c=$("#active-addresses");var d=c.find("table");d.find("tbody").remove();for(var k in j){var b=j[k];if(k==0){d.append('<tbody><tr><th colspan="2">Group #'+k+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{d.append('<tbody><tr><th colspan="4">Group #'+k+"</th></tr></tbody>")}var m=$("<tbody></tbody>").appendTo(d);for(var g in b){var f=b[g];var l=addresses[f];if(!l){continue}var e="";if(l.tag==1){e=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(l.priv==null){e=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var a="";var n=l.addr;if(l.label!=null){n=l.label;a='<span class="hidden-phone"> - '+l.addr+"</span>"}var h='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+l.addr+'" onclick="showAddressModal(\''+l.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+l.addr+'" target="new">'+n+"</a>"+a+" "+e+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(l.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';m.append(h)}d.append(m)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(f){var j=[];var n=f.filteredTaints;for(var g in n){var d=n[g];var e=-1;for(var m in d){var c=d[m];for(var k in j){if(k!=e&&$.inArray(m,j[k])){if(e>=0){var l=j.splice(e,1);var h=j.splice(k,1);j.push(l.concat(h))}else{j[k].push(g)}e=k}if(e>=0){break}}}if(e==-1){j.push([g])}}buildTable(j);apiGetBalances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(j)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};