function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(b){var m=$("#active-addresses");var a=m.find("table");a.find("tbody").remove();for(var n in b){var j=b[n];if(n==0){a.append('<tbody><tr><th colspan="2">Group #'+n+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{a.append('<tbody><tr><th colspan="4">Group #'+n+"</th></tr></tbody>")}var c=$("<tbody></tbody>").appendTo(a);for(var d in j){var h=j[d];var g=addresses[h];if(!g){continue}var f="";if(g.tag==1){f=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(g.priv==null){f=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var k="";var l=g.addr;if(g.label!=null){l=g.label;k='<span class="hidden-phone"> - '+g.addr+"</span>"}var e='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+g.addr+'" onclick="showAddressModal(\''+g.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+g.addr+'" target="new">'+l+"</a>"+k+" "+f+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(g.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';c.append(e)}a.append(c)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(e){var c=[];var d=e.filteredTaints;for(var h in d){var g=d[h];var f=-1;for(var m in g){var n=g[m];for(var l in c){if(l!=f&&$.inArray(m,c[l])){if(f>=0){var k=c.splice(f,1);var j=c.splice(l,1);c.push(k.concat(j))}else{c[l].push(h)}f=l}if(f>=0){break}}}if(f==-1){c.push([h])}}buildTable(c);apiGetBalances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(c)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};