function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(m){var d=$("#active-addresses");var h=d.find("table");h.find("tbody").remove();for(var a in m){var n=m[a];if(a==0){h.append('<tbody><tr><th colspan="2">Group #'+a+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{h.append('<tbody><tr><th colspan="4">Group #'+a+"</th></tr></tbody>")}var c=$("<tbody></tbody>").appendTo(h);for(var b in n){var g=n[b];var e=addresses[g];if(!e){continue}var f="";if(e.tag==1){f=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(e.priv==null){f=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var l="";var k=e.addr;if(e.label!=null){k=e.label;l='<span class="hidden-phone"> - '+e.addr+"</span>"}var j='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+e.addr+'" onclick="showAddressModal(\''+e.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+e.addr+'" target="new">'+k+"</a>"+l+" "+f+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(e.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';c.append(j)}h.append(c)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(f){var n=[];var d=f.filteredTaints;for(var g in d){var k=d[g];var e=-1;for(var m in k){var h=k[m];for(var c in n){if(c!=e&&$.inArray(m,n[c])){if(e>=0){var j=n.splice(e,1);var l=n.splice(c,1);n.push(j.concat(l))}else{n[c].push(g)}e=c}if(e>=0){break}}}if(e==-1){n.push([g])}}buildTable(n);BlockchainAPI.get_balances(getAllAddresses(),function(a){for(var b in a){addresses[b].balance=a[b].final_balance}buildTable(n)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};