function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(e){var f=$("#active-addresses");var d=f.find("table");d.find("tbody").remove();for(var k in e){var m=e[k];if(k==0){d.append('<tbody><tr><th colspan="2">Group #'+k+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{d.append('<tbody><tr><th colspan="4">Group #'+k+"</th></tr></tbody>")}var a=$("<tbody></tbody>").appendTo(d);for(var c in m){var n=m[c];var j=addresses[n];if(!j){continue}var l="";if(j.tag==1){l=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(j.priv==null){l=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var h="";var b=j.addr;if(j.label!=null){b=j.label;h='<span class="hidden-phone"> - '+j.addr+"</span>"}var g='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+j.addr+'" onclick="showAddressModal(\''+j.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+j.addr+'" target="new">'+b+"</a>"+h+" "+l+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(j.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';a.append(g)}d.append(a)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(c){var h=[];var f=c.filteredTaints;for(var n in f){var k=f[n];var j=-1;for(var g in k){var l=k[g];for(var m in h){if(m!=j&&$.inArray(g,h[m])){if(j>=0){var e=h.splice(j,1);var d=h.splice(m,1);h.push(e.concat(d))}else{h[m].push(n)}j=m}if(j>=0){break}}}if(j==-1){h.push([n])}}buildTable(h);BlockchainAPI.get_balances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(h)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};