function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(n){var l=$("#active-addresses");var f=l.find("table");f.find("tbody").remove();for(var b in n){var d=n[b];if(b==0){f.append('<tbody><tr><th colspan="2">Group #'+b+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{f.append('<tbody><tr><th colspan="4">Group #'+b+"</th></tr></tbody>")}var m=$("<tbody></tbody>").appendTo(f);for(var k in d){var a=d[k];var c=addresses[a];if(!c){continue}var j="";if(c.tag==1){j=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(c.priv==null){j=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var e="";var h=c.addr;if(c.label!=null){h=c.label;e='<span class="hidden-phone"> - '+c.addr+"</span>"}var g='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+c.addr+'" onclick="showAddressModal(\''+c.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+c.addr+'" target="new">'+h+"</a>"+e+" "+j+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(c.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';m.append(g)}f.append(m)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(k){var n=[];var l=k.filteredTaints;for(var d in l){var f=l[d];var m=-1;for(var e in f){var j=f[e];for(var c in n){if(c!=m&&$.inArray(e,n[c])){if(m>=0){var g=n.splice(m,1);var h=n.splice(c,1);n.push(g.concat(h))}else{n[c].push(d)}m=c}if(m>=0){break}}}if(m==-1){n.push([d])}}buildTable(n);apiGetBalances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(n)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};