function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(j){var n=$("#active-addresses");var d=n.find("table");d.find("tbody").remove();for(var c in j){var k=j[c];if(c==0){d.append('<tbody><tr><th colspan="2">Group #'+c+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{d.append('<tbody><tr><th colspan="4">Group #'+c+"</th></tr></tbody>")}var m=$("<tbody></tbody>").appendTo(d);for(var h in k){var l=k[h];var g=addresses[l];if(!g){continue}var f="";if(g.tag==1){f=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(g.priv==null){f=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var e="";var b=g.addr;if(g.label!=null){b=g.label;e='<span class="hidden-phone"> - '+g.addr+"</span>"}var a='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+g.addr+'" onclick="showAddressModal(\''+g.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+g.addr+'" target="new">'+b+"</a>"+e+" "+f+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(g.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';m.append(a)}d.append(m)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(g){var e=[];var l=g.filteredTaints;for(var f in l){var j=l[f];var h=-1;for(var m in j){var n=j[m];for(var d in e){if(d!=h&&$.inArray(m,e[d])){if(h>=0){var c=e.splice(h,1);var k=e.splice(d,1);e.push(c.concat(k))}else{e[d].push(f)}h=d}if(h>=0){break}}}if(h==-1){e.push([f])}}buildTable(e);apiGetBalances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(e)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};