function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(n){var k=$("#active-addresses");var h=k.find("table");h.find("tbody").remove();for(var a in n){var f=n[a];if(a==0){h.append('<tbody><tr><th colspan="2">Group #'+a+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{h.append('<tbody><tr><th colspan="4">Group #'+a+"</th></tr></tbody>")}var e=$("<tbody></tbody>").appendTo(h);for(var b in f){var j=f[b];var l=addresses[j];if(!l){continue}var g="";if(l.tag==1){g=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(l.priv==null){g=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var m="";var c=l.addr;if(l.label!=null){c=l.label;m='<span class="hidden-phone"> - '+l.addr+"</span>"}var d='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+l.addr+'" onclick="showAddressModal(\''+l.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+l.addr+'" target="new">'+c+"</a>"+m+" "+g+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(l.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';e.append(d)}h.append(e)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(h){var n=[];var d=h.filteredTaints;for(var l in d){var e=d[l];var k=-1;for(var m in e){var f=e[m];for(var c in n){if(c!=k&&$.inArray(m,n[c])){if(k>=0){var j=n.splice(k,1);var g=n.splice(c,1);n.push(j.concat(g))}else{n[c].push(l)}k=c}if(k>=0){break}}}if(k==-1){n.push([l])}}buildTable(n);apiGetBalances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(n)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};