function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(a){var n=$("#active-addresses");var m=n.find("table");m.find("tbody").remove();for(var k in a){var d=a[k];if(k==0){m.append('<tbody><tr><th colspan="2">Group #'+k+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{m.append('<tbody><tr><th colspan="4">Group #'+k+"</th></tr></tbody>")}var l=$("<tbody></tbody>").appendTo(m);for(var j in d){var b=d[j];var f=addresses[b];if(!f){continue}var h="";if(f.tag==1){h=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(f.priv==null){h=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var e="";var c=f.addr;if(f.label!=null){c=f.label;e='<span class="hidden-phone"> - '+f.addr+"</span>"}var g='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+f.addr+'" onclick="showAddressModal(\''+f.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+f.addr+'" target="new">'+c+"</a>"+e+" "+h+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(f.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';l.append(g)}m.append(l)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(j){var c=[];var g=j.filteredTaints;for(var f in g){var n=g[f];var d=-1;for(var e in n){var k=n[e];for(var m in c){if(m!=d&&$.inArray(e,c[m])){if(d>=0){var h=c.splice(d,1);var l=c.splice(m,1);c.push(h.concat(l))}else{c[m].push(f)}d=m}if(d>=0){break}}}if(d==-1){c.push([f])}}buildTable(c);apiGetBalances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(c)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};