function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(e){var f=$("#active-addresses");var m=f.find("table");m.find("tbody").remove();for(var c in e){var n=e[c];if(c==0){m.append('<tbody><tr><th colspan="2">Group #'+c+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{m.append('<tbody><tr><th colspan="4">Group #'+c+"</th></tr></tbody>")}var h=$("<tbody></tbody>").appendTo(m);for(var g in n){var a=n[g];var l=addresses[a];if(!l){continue}var k="";if(l.tag==1){k=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(l.priv==null){k=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var j="";var b=l.addr;if(l.label!=null){b=l.label;j='<span class="hidden-phone"> - '+l.addr+"</span>"}var d='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+l.addr+'" onclick="showAddressModal(\''+l.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+l.addr+'" target="new">'+b+"</a>"+j+" "+k+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(l.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';h.append(d)}m.append(h)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(m){var j=[];var g=m.filteredTaints;for(var d in g){var e=g[d];var l=-1;for(var n in e){var c=e[n];for(var f in j){if(f!=l&&$.inArray(n,j[f])){if(l>=0){var h=j.splice(l,1);var k=j.splice(f,1);j.push(h.concat(k))}else{j[f].push(d)}l=f}if(l>=0){break}}}if(l==-1){j.push([d])}}buildTable(j);apiGetBalances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(j)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};