function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(m){var h=$("#active-addresses");var l=h.find("table");l.find("tbody").remove();for(var j in m){var f=m[j];if(j==0){l.append('<tbody><tr><th colspan="2">Group #'+j+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{l.append('<tbody><tr><th colspan="4">Group #'+j+"</th></tr></tbody>")}var n=$("<tbody></tbody>").appendTo(l);for(var e in f){var c=f[e];var d=addresses[c];if(!d){continue}var a="";if(d.tag==1){a=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(d.priv==null){a=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var g="";var b=d.addr;if(d.label!=null){b=d.label;g='<span class="hidden-phone"> - '+d.addr+"</span>"}var k='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+d.addr+'" onclick="showAddressModal(\''+d.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+d.addr+'" target="new">'+b+"</a>"+g+" "+a+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(d.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';n.append(k)}l.append(n)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(l){var j=[];var n=l.filteredTaints;for(var e in n){var d=n[e];var m=-1;for(var h in d){var f=d[h];for(var g in j){if(g!=m&&$.inArray(h,j[g])){if(m>=0){var c=j.splice(m,1);var k=j.splice(g,1);j.push(c.concat(k))}else{j[g].push(e)}m=g}if(m>=0){break}}}if(m==-1){j.push([e])}}buildTable(j);BlockchainAPI.get_balances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(j)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};