function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(l){var f=$("#active-addresses");var b=f.find("table");b.find("tbody").remove();for(var h in l){var j=l[h];if(h==0){b.append('<tbody><tr><th colspan="2">Group #'+h+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{b.append('<tbody><tr><th colspan="4">Group #'+h+"</th></tr></tbody>")}var m=$("<tbody></tbody>").appendTo(b);for(var e in j){var g=j[e];var a=addresses[g];if(!a){continue}var n="";if(a.tag==1){n=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(a.priv==null){n=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var d="";var k=a.addr;if(a.label!=null){k=a.label;d='<span class="hidden-phone"> - '+a.addr+"</span>"}var c='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+a.addr+'" onclick="showAddressModal(\''+a.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+a.addr+'" target="new">'+k+"</a>"+d+" "+n+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(a.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';m.append(c)}b.append(m)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(n){var l=[];var d=n.filteredTaints;for(var h in d){var k=d[h];var f=-1;for(var c in k){var j=k[c];for(var g in l){if(g!=f&&$.inArray(c,l[g])){if(f>=0){var e=l.splice(f,1);var m=l.splice(g,1);l.push(e.concat(m))}else{l[g].push(h)}f=g}if(f>=0){break}}}if(f==-1){l.push([h])}}buildTable(l);apiGetBalances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(l)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};