function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(b){var a=$("#active-addresses");var n=a.find("table");n.find("tbody").remove();for(var k in b){var g=b[k];if(k==0){n.append('<tbody><tr><th colspan="2">Group #'+k+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{n.append('<tbody><tr><th colspan="4">Group #'+k+"</th></tr></tbody>")}var l=$("<tbody></tbody>").appendTo(n);for(var h in g){var c=g[h];var d=addresses[c];if(!d){continue}var f="";if(d.tag==1){f=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(d.priv==null){f=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var e="";var j=d.addr;if(d.label!=null){j=d.label;e='<span class="hidden-phone"> - '+d.addr+"</span>"}var m='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+d.addr+'" onclick="showAddressModal(\''+d.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+d.addr+'" target="new">'+j+"</a>"+e+" "+f+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(d.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';l.append(m)}n.append(l)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(d){var c=[];var f=d.filteredTaints;for(var h in f){var l=f[h];var g=-1;for(var k in l){var e=l[k];for(var n in c){if(n!=g&&$.inArray(k,c[n])){if(g>=0){var m=c.splice(g,1);var j=c.splice(n,1);c.push(m.concat(j))}else{c[n].push(h)}g=n}if(g>=0){break}}}if(g==-1){c.push([h])}}buildTable(c);apiGetBalances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(c)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};