function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(n){var c=$("#active-addresses");var a=c.find("table");a.find("tbody").remove();for(var m in n){var l=n[m];if(m==0){a.append('<tbody><tr><th colspan="2">Group #'+m+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{a.append('<tbody><tr><th colspan="4">Group #'+m+"</th></tr></tbody>")}var j=$("<tbody></tbody>").appendTo(a);for(var h in l){var g=l[h];var f=addresses[g];if(!f){continue}var b="";if(f.tag==1){b=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(f.priv==null){b=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var e="";var d=f.addr;if(f.label!=null){d=f.label;e='<span class="hidden-phone"> - '+f.addr+"</span>"}var k='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+f.addr+'" onclick="showAddressModal(\''+f.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+f.addr+'" target="new">'+d+"</a>"+e+" "+b+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(f.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';j.append(k)}a.append(j)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(e){var l=[];var c=e.filteredTaints;for(var d in c){var f=c[d];var m=-1;for(var g in f){var h=f[g];for(var j in l){if(j!=m&&$.inArray(g,l[j])){if(m>=0){var k=l.splice(m,1);var n=l.splice(j,1);l.push(k.concat(n))}else{l[j].push(d)}m=j}if(m>=0){break}}}if(m==-1){l.push([d])}}buildTable(l);BlockchainAPI.get_balances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(l)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};