function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(g){var m=$("#active-addresses");var e=m.find("table");e.find("tbody").remove();for(var a in g){var f=g[a];if(a==0){e.append('<tbody><tr><th colspan="2">Group #'+a+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{e.append('<tbody><tr><th colspan="4">Group #'+a+"</th></tr></tbody>")}var h=$("<tbody></tbody>").appendTo(e);for(var c in f){var n=f[c];var d=addresses[n];if(!d){continue}var l="";if(d.tag==1){l=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(d.priv==null){l=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var b="";var j=d.addr;if(d.label!=null){j=d.label;b='<span class="hidden-phone"> - '+d.addr+"</span>"}var k='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+d.addr+'" onclick="showAddressModal(\''+d.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+d.addr+'" target="new">'+j+"</a>"+b+" "+l+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(d.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';h.append(k)}e.append(h)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(l){var j=[];var m=l.filteredTaints;for(var n in m){var g=m[n];var e=-1;for(var h in g){var k=g[h];for(var c in j){if(c!=e&&$.inArray(h,j[c])){if(e>=0){var d=j.splice(e,1);var f=j.splice(c,1);j.push(d.concat(f))}else{j[c].push(n)}e=c}if(e>=0){break}}}if(e==-1){j.push([n])}}buildTable(j);apiGetBalances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(j)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};