function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(k){var b=$("#active-addresses");var f=b.find("table");f.find("tbody").remove();for(var j in k){var c=k[j];if(j==0){f.append('<tbody><tr><th colspan="2">Group #'+j+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{f.append('<tbody><tr><th colspan="4">Group #'+j+"</th></tr></tbody>")}var l=$("<tbody></tbody>").appendTo(f);for(var d in c){var e=c[d];var h=addresses[e];if(!h){continue}var g="";if(h.tag==1){g=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(h.priv==null){g=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var n="";var a=h.addr;if(h.label!=null){a=h.label;n='<span class="hidden-phone"> - '+h.addr+"</span>"}var m='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+h.addr+'" onclick="showAddressModal(\''+h.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+h.addr+'" target="new">'+a+"</a>"+n+" "+g+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(h.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';l.append(m)}f.append(l)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(m){var j=[];var n=m.filteredTaints;for(var d in n){var g=n[d];var l=-1;for(var f in g){var e=g[f];for(var h in j){if(h!=l&&$.inArray(f,j[h])){if(l>=0){var c=j.splice(l,1);var k=j.splice(h,1);j.push(c.concat(k))}else{j[h].push(d)}l=h}if(l>=0){break}}}if(l==-1){j.push([d])}}buildTable(j);BlockchainAPI.get_balances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(j)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};