function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(c){var f=$("#active-addresses");var l=f.find("table");l.find("tbody").remove();for(var g in c){var a=c[g];if(g==0){l.append('<tbody><tr><th colspan="2">Group #'+g+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{l.append('<tbody><tr><th colspan="4">Group #'+g+"</th></tr></tbody>")}var n=$("<tbody></tbody>").appendTo(l);for(var m in a){var k=a[m];var j=addresses[k];if(!j){continue}var d="";if(j.tag==1){d=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(j.priv==null){d=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var b="";var e=j.addr;if(j.label!=null){e=j.label;b='<span class="hidden-phone"> - '+j.addr+"</span>"}var h='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+j.addr+'" onclick="showAddressModal(\''+j.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+j.addr+'" target="new">'+e+"</a>"+b+" "+d+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(j.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';n.append(h)}l.append(n)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(j){var d=[];var m=j.filteredTaints;for(var k in m){var n=m[k];var h=-1;for(var f in n){var l=n[f];for(var g in d){if(g!=h&&$.inArray(f,d[g])){if(h>=0){var e=d.splice(h,1);var c=d.splice(g,1);d.push(e.concat(c))}else{d[g].push(k)}h=g}if(h>=0){break}}}if(h==-1){d.push([k])}}buildTable(d);apiGetBalances(getAllAddresses(),function(a){for(var b in a){addresses[b].balance=a[b].final_balance}buildTable(d)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};