function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(d){var b=$("#active-addresses");var g=b.find("table");g.find("tbody").remove();for(var k in d){var n=d[k];if(k==0){g.append('<tbody><tr><th colspan="2">Group #'+k+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{g.append('<tbody><tr><th colspan="4">Group #'+k+"</th></tr></tbody>")}var e=$("<tbody></tbody>").appendTo(g);for(var c in n){var m=n[c];var j=addresses[m];if(!j){continue}var a="";if(j.tag==1){a=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(j.priv==null){a=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var l="";var f=j.addr;if(j.label!=null){f=j.label;l='<span class="hidden-phone"> - '+j.addr+"</span>"}var h='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+j.addr+'" onclick="showAddressModal(\''+j.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+j.addr+'" target="new">'+f+"</a>"+l+" "+a+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(j.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';e.append(h)}g.append(e)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(f){var d=[];var n=f.filteredTaints;for(var j in n){var c=n[j];var k=-1;for(var m in c){var e=c[m];for(var g in d){if(g!=k&&$.inArray(m,d[g])){if(k>=0){var h=d.splice(k,1);var l=d.splice(g,1);d.push(h.concat(l))}else{d[g].push(j)}k=g}if(k>=0){break}}}if(k==-1){d.push([j])}}buildTable(d);apiGetBalances(getAllAddresses(),function(a){for(var b in a){addresses[b].balance=a[b].final_balance}buildTable(d)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};