function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(f){var b=$("#active-addresses");var n=b.find("table");n.find("tbody").remove();for(var c in f){var l=f[c];if(c==0){n.append('<tbody><tr><th colspan="2">Group #'+c+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{n.append('<tbody><tr><th colspan="4">Group #'+c+"</th></tr></tbody>")}var g=$("<tbody></tbody>").appendTo(n);for(var h in l){var m=l[h];var k=addresses[m];if(!k){continue}var e="";if(k.tag==1){e=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(k.priv==null){e=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var j="";var d=k.addr;if(k.label!=null){d=k.label;j='<span class="hidden-phone"> - '+k.addr+"</span>"}var a='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+k.addr+'" onclick="showAddressModal(\''+k.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+k.addr+'" target="new">'+d+"</a>"+j+" "+e+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(k.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';g.append(a)}n.append(g)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(l){var f=[];var k=l.filteredTaints;for(var m in k){var c=k[m];var g=-1;for(var e in c){var h=c[e];for(var d in f){if(d!=g&&$.inArray(e,f[d])){if(g>=0){var n=f.splice(g,1);var j=f.splice(d,1);f.push(n.concat(j))}else{f[d].push(m)}g=d}if(g>=0){break}}}if(g==-1){f.push([m])}}buildTable(f);apiGetBalances(getAllAddresses(),function(a){for(var b in a){addresses[b].balance=a[b].final_balance}buildTable(f)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};